import React, { useState } from 'react';
import { Button, Card, Col, Input, Row, Select, Table } from 'antd';
import { BorderOutlined, CloseOutlined, MinusOutlined } from '@ant-design/icons';

const { Option } = Select;

interface DataType {
  key: React.Key;
  item: string;
  category: string;
}

const columns = [
  {
    title: 'Item',
    dataIndex: 'item',
    key: 'item',
  },
  {
    title: 'Category',
    dataIndex: 'category',
    key: 'category',
  },
];

const initialData: DataType[] = [...Array(20)].map((_, index) => ({
  key: index,
  item: `Item ${index}`,
  category: `Category ${index % 5}`,
}));

const App: React.FC = () => {
  const [selectedRowKeys, setSelectedRowKeys] = useState<React.Key[]>([]);
  const [data, setData] = useState<DataType[]>(initialData);
  const [favorites, setFavorites] = useState<DataType[]>([]);
  const [selectedFavoritesKeys, setSelectedFavoritesKeys] = useState<React.Key[]>([]);

  const rowSelection = {
    onChange: (newSelectedRowKeys: React.Key[]) => {
      setSelectedRowKeys(newSelectedRowKeys);
    },
  };

  const favoritesRowSelection = {
    onChange: (newSelectedRowKeys: React.Key[]) => {
      setSelectedFavoritesKeys(newSelectedRowKeys);
    },
  };

  const handleCreateFavorite = () => {
    const newFavorites = data.filter((item) => selectedRowKeys.includes(item.key));
    setFavorites([...favorites, ...newFavorites].reduce((acc, current) => {
      const x = acc.find(item => item.key === current.key);
      if (!x) {
        return acc.concat([current]);
      } else {
        return acc;
      }
    }, [] as DataType[])); // Prevent duplicates
    setSelectedRowKeys([]); // Optionally clear selection after adding
  };

  const handleDeleteFavorite = () => {
    const newFavorites = favorites.filter((item) => !selectedFavoritesKeys.includes(item.key));
    setFavorites(newFavorites);
    setSelectedFavoritesKeys([]); // Clear selection after deleting
  };

  const handleResetFavorites = () => {
    setFavorites([]);
  };

  return (
    <div className="custom-window" style={{ width: '100%', maxWidth: '960px', margin: '0 auto', border: '1px solid #ccc', borderRadius: '5px', overflow: 'hidden' }}>
      {/* Window header and other UI elements remain unchanged */}

      <Row gutter={16} style={{ padding: '20px', backgroundColor: '#f0f0f0' }}>
        <Col span={12}>
          {/* The left side (Items list) remains unchanged */}
        </Col>

        <Col span={12}>
          <Card title="Favorites" style={{ flexGrow: 1 }}>
            <Table
              rowKey="key"
              columns={columns}
              dataSource={favorites}
              pagination={false}
              rowSelection={favoritesRowSelection}
            />
          </Card>
          <div style={{ marginTop: '20px' }}>
            <Button type="primary" onClick={handleDeleteFavorite} disabled={selectedFavoritesKeys.length === 0} style={{ marginRight: '8px' }}>
              Delete Favorite
            </Button>
            <Button type="primary" onClick={handleResetFavorites}>
              Reset Favorites
            </Button>
          </div>
        </Col>
      </Row>
    </div>
  );
};

export default App;
