import React, { useState } from 'react'
import { Card, Table, Row, Col, Input, Select, Button } from 'antd'
import { CloseOutlined, MinusOutlined, BorderOutlined } from '@ant-design/icons'

const { Option } = Select

interface DataType {
  key: React.Key
  item: string
  category: string
}

const columns = [
  {
    title: 'Item',
    dataIndex: 'item',
    key: 'item',
  },
  {
    title: 'Category',
    dataIndex: 'category',
    key: 'category',
  },
]

const initialData: DataType[] = [...Array(20)].map((_, index) => ({
  key: index,
  item: `Item ${index}`,
  category: `Category ${index % 5}`,
}))

const App: React.FC = () => {
  const [selectedRowKeys, setSelectedRowKeys] = useState<React.Key[]>([])
  const [selectedFavoritesKeys, setSelectedFavoritesKeys] = useState<React.Key[]>([])
  const [data, setData] = useState<DataType[]>(initialData)
  const [favorites, setFavorites] = useState<DataType[]>([])
  const [makeFavorite, setMakeFavorite] = useState(false)

  const rowSelection = {
    onChange: (selectedRowKeys: React.Key[], selectedRows: DataType[]) => {
      setSelectedRowKeys(selectedRowKeys)
    },
  }

  const favoritesRowSelection = {
    onChange: (selectedKeys: React.Key[]) => {
      setSelectedFavoritesKeys(selectedKeys)
    },
  }

  const handleCreateFavorite = () => {
    if (makeFavorite) {
      const newFavorites = data.filter((item) => selectedRowKeys.includes(item.key))
      setFavorites([...favorites, ...newFavorites])
    }
  }

  const handleDeleteSelectedFavorites = () => {
    setFavorites(favorites.filter(item => !selectedFavoritesKeys.includes(item.key)))
    setSelectedFavoritesKeys([]) // Optionally clear selection after deletion
  }

  const handleResetFavorites = () => {
    setFavorites([])
  }

  return (
    <div className="custom-window" style={{ width: '100%', maxWidth: '960px', margin: '0 auto', border: '1px solid #ccc', borderRadius: '5px', overflow: 'hidden' }}>
      <div className="window-header" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '10px', backgroundColor: '#f0f0f0', borderBottom: '1px solid #ccc' }}>
        <h2 style={{ flex: 1 }}>Flight Graphs</h2>
        <Button icon={<MinusOutlined />} />
        <Button icon={<BorderOutlined />} />
        <Button icon={<CloseOutlined />} />
      </div>

      <Row gutter={16} style={{ padding: '20px', backgroundColor: '#f0f0f0' }}>
        {/* Left Column */}
        <Col span={12} style={{ display: 'flex', flexDirection: 'column' }}>
          {/* Existing Table and Controls */}
        </Col>

        {/* Right Column */}
        <Col span={12} style={{ display: 'flex', flexDirection: 'column' }}>
          <Card title="Favorites" style={{ flexGrow: 1 }}>
            <Table rowKey="key" columns={columns} dataSource={favorites} pagination={false} rowSelection={{...favoritesRowSelection, selectedRowKeys: selectedFavoritesKeys}} />
          </Card>
          <div style={{ marginTop: '20px' }}>
            <Button type="primary" style={{ marginRight: '8px' }} onClick={handleDeleteSelectedFavorites}>Delete Selected Favorites</Button>
            <Button type="primary" onClick={handleResetFavorites}>Reset Favorites</Button>
          </div>
        </Col>
      </Row>
    </div>
  )
}

export default App
