import React, { useState } from 'react'
import { Card, Table, Row, Col, Input, Select, Button } from 'antd'
import { CloseOutlined, MinusOutlined, BorderOutlined } from '@ant-design/icons'

const { Option } = Select

interface DataType {
  key: React.Key
  item: string
  category: string
}

const columns = [
  {
    title: 'Item',
    dataIndex: 'item',
    key: 'item',
  },
  {
    title: 'Category',
    dataIndex: 'category',
    key: 'category',
  },
]

const initialData: DataType[] = [...Array(20)].map((_, index) => ({
  key: index,
  item: `Item ${index}`,
  category: `Category ${index % 5}`,
}))

const App: React.FC = () => {
  const [selectedRowKeys, setSelectedRowKeys] = useState<React.Key[]>([])
  const [selectedFavoriteKeys, setSelectedFavoriteKeys] = useState<React.Key[]>([]) // Yeni durum
  const [data, setData] = useState<DataType[]>(initialData)
  const [favorites, setFavorites] = useState<DataType[]>([])
  const [makeFavorite, setMakeFavorite] = useState(false)

  const rowSelection = {
    onChange: (selectedRowKeys: React.Key[], selectedRows: DataType[]) => {
      setSelectedRowKeys(selectedRowKeys)
    },
    getCheckboxProps: (record: DataType) => ({
      disabled: record.item === 'Disabled User',
    }),
  }

  const favoriteRowSelection = { // Favoriler için rowSelection
    onChange: (selectedKeys: React.Key[]) => {
      setSelectedFavoriteKeys(selectedKeys)
    },
  }

  const handleCreateFavorite = () => {
    if (makeFavorite) {
      const newFavorites = data.filter((item) => selectedRowKeys.includes(item.key))
      setFavorites([...favorites, ...newFavorites])
    }
  }

  const deleteSelectedFavorites = () => {
    const remainingFavorites = favorites.filter((fav) => !selectedFavoriteKeys.includes(fav.key))
    setFavorites(remainingFavorites)
    setSelectedFavoriteKeys([]) // Seçili favori öğeleri sıfırla
  }

  const handleResetFavorites = () => {
    setFavorites([])
    setSelectedFavoriteKeys([]) // Favorileri sıfırlarken, seçili favori anahtarlarını da sıfırlayın
  }

  return (
    <div className="custom-window" style={{ width: '100%', maxWidth: '960px', margin: '0 auto', border: '1px solid #ccc', borderRadius: '5px', overflow: 'hidden' }}>
      {/* Kapsayıcı ve başlık yapısı aynı kaldı */}

      <Row gutter={16} style={{ padding: '20px', backgroundColor: '#f0f0f0' }}>
        <Col span={12}>
          {/* Veri listesi ve favori oluşturma butonları aynı kaldı */}
        </Col>
        <Col span={12} style={{ display: 'flex', flexDirection: 'column' }}>
          <Card title="Favorites" style={{ flexGrow: 1 }}>
            {favorites.length > 0 ? (
              <Table rowKey="key" columns={columns} dataSource={favorites} pagination={false} rowSelection={favoriteRowSelection} /> // Favoriler için Table bileşeni güncellendi
            ) : (
              'No favorites selected.'
            )}
          </Card>
          <div style={{ marginTop: '20px' }}>
            <Button type="primary" onClick={deleteSelectedFavorites} style={{ marginRight: '8px' }}>Delete Selected Favorites</Button>
            <Button onClick={handleResetFavorites}>Reset Favorites</Button>
          </div>
        </Col>
      </Row>
    </div>
  )
}

export default App
