import React, { useState } from 'react'
import { Card, Table, Row, Col, Input, Select, Button } from 'antd'
import { CloseOutlined, MinusOutlined, BorderOutlined } from '@ant-design/icons'

const { Option } = Select

interface DataType {
  key: React.Key
  item: string
  category: string
}

const columns = [
  {
    title: 'Item',
    dataIndex: 'item',
    key: 'item',
  },
  {
    title: 'Category',
    dataIndex: 'category',
    key: 'category',
  },
]

const initialData: DataType[] = [...Array(20)].map((_, index) => ({
  key: index,
  item: `Item ${index}`,
  category: `Category ${index % 5}`,
}))

const App: React.FC = () => {
  const [selectedRowKeys, setSelectedRowKeys] = useState<React.Key[]>([])
  const [selectedFavoriteKeys, setSelectedFavoriteKeys] = useState<React.Key[]>([])
  const [data, setData] = useState<DataType[]>(initialData)
  const [favorites, setFavorites] = useState<DataType[]>([])
  const [makeFavorite, setMakeFavorite] = useState(false)

  const rowSelection = {
    onChange: (selectedRowKeys: React.Key[], selectedRows: DataType[]) => {
      setSelectedRowKeys(selectedRowKeys)
    },
  }

  const favoriteRowSelection = {
    onChange: (selectedRowKeys: React.Key[]) => {
      setSelectedFavoriteKeys(selectedRowKeys)
    },
  }

  const handleCreateFavorite = () => {
    if (makeFavorite) {
      const newFavorites = data.filter((item) => selectedRowKeys.includes(item.key))
      setFavorites([...favorites, ...newFavorites])
    }
  }

  const handleDeleteSelectedFavorites = () => {
    setFavorites(favorites.filter((item) => !selectedFavoriteKeys.includes(item.key)))
    setSelectedFavoriteKeys([]) // Clear selection after deletion
  }

  const handleResetFavorites = () => {
    setFavorites([])
  }

  return (
    <div className="custom-window" style={{ width: '100%', maxWidth: '960px', margin: '0 auto', border: '1px solid #ccc', borderRadius: '5px', overflow: 'hidden' }}>
      {/* Header and other components remain unchanged */}

      <Row gutter={16} style={{ padding: '20px', backgroundColor: '#f0f0f0' }}>
        {/* Left side (items table) remains unchanged */}

        <Col span={12} style={{ display: 'flex', flexDirection: 'column' }}>
          <Card title="Favorites" style={{ flexGrow: 1 }}>
            <Table rowKey="key" columns={columns} dataSource={favorites} pagination={false} rowSelection={favoriteRowSelection} />
          </Card>
          <div style={{ marginTop: '20px' }}>
            <Button type="primary" style={{ marginRight: '8px' }} onClick={handleDeleteSelectedFavorites}>Delete Favorite</Button>
            <Button type="primary" onClick={handleResetFavorites}>Reset Favorites</Button>
          </div>
        </Col>
      </Row>
    </div>
  )
}

export default App
